package controlStatment;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String day="thursday";
		
		switch (day) {
		case "sunday":System.out.println("This is first day of the week");
			
			break;
		case "monday":System.out.println("This is second day of the week");
		break;
		
		case "tuesday":System.out.println("This is third day of the week");
		break;
		
		case "wednesday":System.out.println("This is fourth day of the week");
		break;
		
		case"thursday":System.out.println("This is fifth day of the week");
		break;
		
		case"friday":System.out.println("This is sixth day of the week");
		break;
		
		case"saturday":System.out.println("This is last day of the week");
		break;
		
		
		
		default:
			break;
		}
		
		
		
		
		

	}

}
